mapper.select:
SELECT id AS "id", email AS "email", name AS "name", password AS "password", login_name AS "login_name", user_catagory AS "userCatagory" FROM sys_user WHERE del_flag = 0
AND login_name LIKE #{filter.login_name}
AND name LIKE #{filter.name}
AND email LIKE #{filter.email}
ORDER BY id DESC
LIMIT #{filter.first},#{filter.last}

SELECT COUNT(id) FROM sys_user WHERE del_flag = 0
AND login_name LIKE #{filter.login_name}
AND email LIKE #{filter.email}

mapper.update:
UPDATE
  sys_user
SET
  password = #{password}
WHERE
  id = #{id}

mapper.select:
SELECT id AS "id", name AS "name", office_id AS "officeId" FROM sys_user WHERE del_flag = 0
AND office_id = #{filter.officeId}
AND id = #{filter.userId}

SELECT u.id AS "id", u.name AS "name", o.area_id AS "areaId" FROM sys_user u, sys_office o WHERE u.del_flag = 0 AND o.del_flag = 0 AND u.office_id = o.id AND u.user_catagory = 1
AND o.area_id = #{filter.areaId}
AND u.id = #{filter.userId}

SELECT u.id AS "id", u.name AS "name" FROM sys_user u, sys_office o, sys_role r, sys_user_role ur WHERE u.del_flag = 0 AND o.del_flag = 0 AND r.del_flag = 0 AND u.office_id = o.id AND ur.user_id = u.id AND ur.role_id = r.id AND u.user_catagory = 2
AND o.area_id = #{areaId}
AND r.role_sign = #{roleSign}
