mapper.select:
        SELECT
          COUNT(id)
        FROM
          st_product



	            OR product_name LIKE #{filter.search}



	            OR id LIKE #{filter.search}

SELECT p.id AS "productId", brand_name AS "brandName", product_name AS "productName", product_num AS "productNum", brand_id AS "brandId", spec_value AS "specValue", unit_value AS "unitValue", catalog_fee AS "catalogFee", p.del_flag AS "delFlag" FROM st_product p left join st_product_brand pb ON (p.brand_id = pb.id)
AND p.id IN
#{id}
