mapper.select:
SELECT stock_number as stockNumber, stock_order_id as stockOrderId, area_id as areaId, user_id as userId, product_id as productId , brand_id as brandId, stock_fee as stockFee , supplier_id as supplierId , if_user_oder_transition as ifUserOderTransition , register_number as registerNumber , user_order_id as userOrderId, order_num as orderNum , status_cd as statusCd , accept_date as acceptDate , change_date as changeDate, if_auto_allot as ifAutoAllot , allot_user_id as allotUserId, buyer_user_id as buyerUserId FROM st_stock_product_order WHERE 1= 1
and stock_number =#{stockProductOrder.stockNumber}
and stock_order_id =#{stockProductOrder.stockOrderId}
and status_cd =#{stockProductOrder.statusCd}
and brand_id =#{stockProductOrder.brandId}
and product_id =#{stockProductOrder.productId}
and supplier_id =#{filter.supplierId}
and user_id =#{filter.userId}
and DATE_FORMAT(accept_date, '%Y-%m-%d') >= DATE_FORMAT(#{filter.startDate}, '%Y-%m-%d')
and DATE_FORMAT(accept_date, '%Y-%m-%d') <= DATE_FORMAT(#{filter.endDate}, '%Y-%m-%d')
order by stock_number desc
LIMIT #{filter.first}, #{filter.last}

SELECT stock_number as stockNumber, stock_order_id as stockOrderId, area_id as areaId, user_id as userId, product_id as productId , brand_id as brandId, stock_fee as stockFee , supplier_id as supplierId , if_user_oder_transition as ifUserOderTransition , register_number as registerNumber , user_order_id as userOrderId, order_num as orderNum , status_cd as statusCd , accept_date as acceptDate , change_date as changeDate, if_auto_allot as ifAutoAllot , allot_user_id as allotUserId, buyer_user_id as buyerUserId FROM st_stock_product_order
stock_number =#{stockNumber}
and stock_order_id =#{stockOrderId}
and status_cd =#{statusCd}
and brand_id =#{brandId}
and product_id =#{productId}
and buyer_user_id =#{buyerUserId}

SELECT
	count(*)
FROM
	st_stock_product_order


		stock_number =#{stockNumber}


		and stock_order_id =#{stockOrderId}


		and status_cd =#{statusCd}


		and brand_id =#{brandId}


		and product_id =#{productId}


		and buyer_user_id =#{buyerUserId}
