mapper.update:
update st_user_order set
	status_Cd = #{statusCd}
where user_order_id = #{userOrderId}

mapper.select:
		select
			user_order_id as userOrderId,
			user_id as userId,
			area_id as areaId,
			group_id as groupId,
			address_id as addressId,
			accept_date as acceptDate,
			status_cd as statusCd
		from st_user_order


				area_id =#{userOrder.areaId}


				and user_order_id =#{userOrder.userOrderId}


			 	and group_id =#{userOrder.groupId}


			    and user_id =#{userOrder.userId}


				 and DATE_FORMAT(accept_date, '%Y-%m-%d') >=  DATE_FORMAT(#{filter.startDate}, '%Y-%m-%d') 


				 and DATE_FORMAT(accept_date, '%Y-%m-%d') <=  DATE_FORMAT(#{filter.endDate}, '%Y-%m-%d') 


			    and status_cd = #{userOrder.statusCd}


			    and status_cd in 

					#{item}



		order by accept_date desc

            LIMIT #{filter.first}, #{filter.last}

select
	count(user_order_id)
from st_user_order


		area_id =#{userOrder.areaId}


		and user_order_id =#{userOrder.userOrderId}


	 	and group_id =#{userOrder.groupId}


	    and user_id =#{userOrder.userId}


	    and status_cd =#{userOrder.statusCd}


	    and status_cd in 

			#{item}
