mapper.select:

SELECT
  id AS "id",
  name AS "name",
  remark AS "remark",
  type AS "type",
  value AS "value",
  fk_category_id AS "fk_category_id"
FROM
  tb_data_dictionary




        name LIKE #{filter.name}




        AND value LIKE #{filter.value}



        AND fk_category_id = #{filter.fk_category_id}



        AND type = #{filter.type}



ORDER BY id DESC

    LIMIT #{filter.first},#{filter.last}


SELECT
  count(id)
FROM
  tb_data_dictionary




        name LIKE #{filter.name}




        AND value LIKE #{filter.value}



        AND fk_category_id = #{filter.fk_category_id}



        AND type = #{filter.type}
