mapper.select:
SELECT id AS "id", code AS "code", name AS "name", remark AS "remark" FROM tb_dictionary_category
WHERE id NOT IN
#{id}

SELECT id AS "id", code AS "code", name AS "name", remark AS "remark" FROM tb_dictionary_category
code = #{filter.code}
AND name LIKE #{filter.name}
ORDER BY id DESC
LIMIT #{filter.first},#{filter.last}


SELECT
  COUNT(id)
FROM
  tb_dictionary_category


        code = #{filter.code}




        AND name LIKE #{filter.name}

